/**
 * PURPOSE
 * Parses the raw webhook form submission body into the canonical
 * trigger.v1.family_tax_form schema. Validates all required fields
 * for the quick snapshot (sections 1-3) and normalises data types.
 * If deep dive fields (sections 4-8) are present, includes them.
 *
 * v3.0.0 CHANGES:
 * - work_from_home now extracted from BASE payload (was deep dive only)
 * - private_health now extracted from BASE payload (was deep dive only)
 * - existing_benefits NEW field extracted from base payload
 * - Backward compatible: missing v3 fields default to "unknown" with warning
 * - Deep dive detection updated: WFH and PHI no longer trigger deep dive alone
 *
 * INPUTS
 * - Schema: trigger.v1.family_tax_form (raw webhook body)
 *   Source: $('T1 Webhook Form Submit') â€” trigger node reference
 * - Config: $('C1 Config') â€” validation rules, enums
 *
 * REMOVALS
 * - Raw webhook metadata fields not needed downstream
 *
 * ADDITIONS
 * - trigger.v1.family_tax_form structured object
 * - form_type (string): "quick_snapshot" or "deep_dive"
 * - meta object with run_id, valid, warnings
 * - __audit object
 *
 * ROW GUIDE
 * Input: { "body": { "state": "VIC", ... } }
 * Output: { "trigger": { "v1": { ... } }, "meta": { ... }, "__audit": { ... } }
 *
 * ASSUMPTIONS
 * - T1 Webhook delivers JSON body via POST
 * - Webflow sends form fields as strings (numbers need coercion)
 * - Multi-select fields arrive as comma-separated strings
 * - Checkbox fields arrive as "true"/"false" strings
 * - v2.0.0 payloads may arrive without work_from_home/private_health/existing_benefits in base
 *
 * AUDIT
 * Hybrid: __audit + meta.valid=false â†’ DLQ, minor â†’ meta.warnings[]
 */

const NODE = 'P1 Parse & Validate Form Data';

// === INPUTS ===
const triggerData = $('T1 Webhook Form Submit').item.json;
const config = $('C1 Config').item.json;

// === RUN TRACKING ===
const runId = $execution.id || `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

// === HELPERS ===
function normaliseString(val) {
  if (val === null || val === undefined || val === '') return null;
  return String(val).trim().toLowerCase().replace(/[\s-]+/g, '_');
}

function normaliseArray(val) {
  if (Array.isArray(val)) return val.map(v => normaliseString(v)).filter(Boolean);
  if (typeof val === 'string' && val.trim()) {
    return val.split(',').map(v => normaliseString(v)).filter(Boolean);
  }
  return [];
}

function normaliseBoolean(val) {
  if (val === true || val === 'true' || val === 'on' || val === '1' || val === 'yes') return true;
  if (val === false || val === 'false' || val === 'off' || val === '0' || val === 'no') return false;
  return null;
}

function normaliseNumber(val) {
  if (val === null || val === undefined || val === '') return null;
  const num = Number(val);
  return isNaN(num) ? null : num;
}

function isValidEmail(email) {
  if (!email) return false;
  return /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/.test(email);
}

// === EXTRACT SOURCE DATA ===
const src = triggerData.body || triggerData;
const beforeKeys = Object.keys(src);

const errors = [];
const warnings = [];

const client_submission_id = (src.client_submission_id || '').trim();
const honeypot_website = (src.website || '').trim();
const webhook_token = (src.webhook_token || '').trim();
const request_origin = triggerData.headers ? (triggerData.headers.origin || triggerData.headers.Origin || '') : '';


// --- Section 1: Household Basics ---
const state = (src.state || '').trim().toUpperCase();
const relationship_status = normaliseString(src.relationship_status);
const num_dependants = normaliseNumber(src.num_dependants) ?? 0;
const dependant_ages = normaliseArray(src.dependant_ages);
const adult_ages = normaliseArray(src.adult_ages);
const existing_benefits = normaliseArray(src.existing_benefits);  // v3.0.0

// --- Section 2: Income Profile ---
const household_income_range = normaliseString(src.household_income_range);
const income_split = normaliseString(src.income_split);
const income_types = normaliseArray(src.income_types);
const salary_sacrifice = normaliseString(src.salary_sacrifice);
const work_from_home = normaliseString(src.work_from_home);  // v3.0.0: promoted

// --- Section 3: Property & Assets ---
const home_status = normaliseString(src.home_status);
const investment_property = normaliseString(src.investment_property);
const shares_or_funds = normaliseString(src.shares_or_funds);
const crypto = normaliseString(src.crypto);
const private_health = normaliseString(src.private_health);  // v3.0.0: promoted

// --- Contact ---
const first_name = (src.first_name || '').trim();
const email = (src.email || '').trim().toLowerCase();
const phone = (src.phone || '').trim() || null;
const consent_marketing = normaliseBoolean(src.consent_marketing) || false;
const consent_disclaimer = normaliseBoolean(src.consent_disclaimer);

// --- Deep Dive Detection (v3.0.0 updated) ---
// WFH and PHI are no longer deep-dive-only signals.
// Deep dive is detected by presence of sections 4-8 specific fields.
const has_deep_dive = !!(
  src.super_balance_range || src.voluntary_contributions ||
  src.motor_vehicle_work || src.deduction_flags ||
  src.childcare_type || src.life_events ||
  src.phi_cover_level || src.phi_all_covered
);

// --- Deep Dive Sections (4-8, optional) ---
const deep_dive = has_deep_dive ? {
  super: {
    super_balance_range: normaliseString(src.super_balance_range),
    voluntary_contributions: normaliseString(src.voluntary_contributions),
    super_fund_type: normaliseArray(src.super_fund_type),
    spouse_super_low: normaliseString(src.spouse_super_low)
  },
  deductions: {
    motor_vehicle_work: normaliseString(src.motor_vehicle_work),
    uniform_clothing: normaliseString(src.uniform_clothing),
    self_education: normaliseString(src.self_education),
    union_fees: normaliseString(src.union_fees),
    income_protection: normaliseString(src.income_protection),
    donations: normaliseString(src.donations),
    deduction_flags: normaliseArray(src.deduction_flags)
  },
  health: {
    phi_cover_level: normaliseString(src.phi_cover_level),
    phi_all_covered: normaliseString(src.phi_all_covered)
  },
  children: {
    childcare_type: normaliseArray(src.childcare_type),
    childcare_days: normaliseNumber(src.childcare_days),
    child_disability: normaliseString(src.child_disability)
  },
  life_events: normaliseArray(src.life_events)
} : null;

// === VALIDATION ===
const validConfig = config.validation_config;

if (!state) errors.push('Missing required: state');
else if (!validConfig.valid_states.includes(state)) errors.push(`Invalid state: ${state}`);

if (!relationship_status) errors.push('Missing required: relationship_status');
else if (!validConfig.valid_relationship_statuses.includes(relationship_status))
  errors.push(`Invalid relationship_status: ${relationship_status}`);

if (num_dependants > 0 && dependant_ages.length === 0)
  warnings.push('Dependants declared but no ages provided');

if (adult_ages.length === 0) errors.push('Missing required: adult_ages');

if (!household_income_range) errors.push('Missing required: household_income_range');
else if (!validConfig.valid_income_ranges.includes(household_income_range))
  errors.push(`Invalid household_income_range: ${household_income_range}`);

if (!income_split) errors.push('Missing required: income_split');
else if (!validConfig.valid_income_splits.includes(income_split))
  errors.push(`Invalid income_split: ${income_split}`);

if (income_types.length === 0) errors.push('Missing required: income_types (at least one)');

if (!home_status) errors.push('Missing required: home_status');
else if (!validConfig.valid_home_statuses.includes(home_status))
  errors.push(`Invalid home_status: ${home_status}`);

if (!first_name) errors.push('Missing required: first_name');
if (!email) errors.push('Missing required: email');
else if (!isValidEmail(email)) errors.push(`Invalid email format: ${email}`);

if (consent_disclaimer !== true) errors.push('Missing required: consent_disclaimer must be true');

// v3.0.0: Validate promoted fields (non-blocking â€” backward compat)
if (work_from_home && validConfig.valid_work_from_home &&
    !validConfig.valid_work_from_home.includes(work_from_home)) {
  warnings.push(`Unexpected work_from_home value: ${work_from_home}`);
}
if (private_health && validConfig.valid_private_health &&
    !validConfig.valid_private_health.includes(private_health)) {
  warnings.push(`Unexpected private_health value: ${private_health}`);
}

// v3.0.0: Backward compatibility â€” flag if promoted fields missing (v2 form)
if (!work_from_home && !has_deep_dive) {
  warnings.push('work_from_home not provided (v2 form or unanswered) â€” defaulting to unknown');
}
if (!private_health && !has_deep_dive) {
  warnings.push('private_health not provided (v2 form or unanswered) â€” defaulting to unknown');
}

if (!phone) warnings.push('No phone provided (optional)');
if (salary_sacrifice === 'not_sure') warnings.push('User unsure about salary sacrifice status');


// Security checks
const securityConfig = config.security_config || {};
if (honeypot_website) errors.push('Blocked by anti-spam honeypot');
if (securityConfig.require_webhook_token === true) {
  if (!webhook_token || webhook_token !== securityConfig.webhook_token) {
    errors.push('Invalid webhook_token');
  }
}
if (Array.isArray(securityConfig.allowed_origins) && securityConfig.allowed_origins.length > 0) {
  if (request_origin && !securityConfig.allowed_origins.includes(request_origin)) {
    warnings.push(`Unexpected origin: ${request_origin}`);
  }
}

// Lightweight dedupe guard (global workflow static state)
const dedupeConfig = config.dedupe_config || {};
if (dedupeConfig.enabled !== false) {
  const dedupeKey = client_submission_id || `${email}|${state}|${household_income_range}|${new Date().toISOString().slice(0,10)}`;
  if (typeof $getWorkflowStaticData === 'function' && dedupeKey) {
    const store = $getWorkflowStaticData('global');
    store.fta_seen = store.fta_seen || {};
    const windowMs = (dedupeConfig.window_minutes || 30) * 60 * 1000;
    const now = Date.now();

    Object.keys(store.fta_seen).forEach((k) => {
      if ((now - store.fta_seen[k]) > windowMs) delete store.fta_seen[k];
    });

    if (store.fta_seen[dedupeKey] && (now - store.fta_seen[dedupeKey]) <= windowMs) {
      errors.push('Duplicate submission detected in dedupe window');
    }
    store.fta_seen[dedupeKey] = now;

    const maxEntries = dedupeConfig.max_entries || 5000;
    const keys = Object.keys(store.fta_seen);
    if (keys.length > maxEntries) {
      keys.sort((a,b)=>store.fta_seen[a]-store.fta_seen[b]).slice(0, keys.length-maxEntries).forEach(k => delete store.fta_seen[k]);
    }
  }
}


const isValid = errors.length === 0;
const formType = has_deep_dive ? 'deep_dive' : 'quick_snapshot';
const formVersion = (src.existing_benefits !== undefined) ? '3.0.0' : '2.0.0';

// === BUILD OUTPUT ===
const output = {
  trigger: {
    v1: {
      type: 'family_tax_form',
      form_type: formType,
      ts: new Date().toISOString(),
      run_id: runId,
      household: {
        state,
        relationship_status,
        num_dependants,
        dependant_ages,
        adult_ages,
        existing_benefits  // v3.0.0
      },
      income: {
        household_income_range,
        income_split,
        income_types,
        salary_sacrifice,
        work_from_home: work_from_home || 'unknown'  // v3.0.0: promoted, with fallback
      },
      property: {
        home_status,
        investment_property,
        shares_or_funds,
        crypto,
        private_health: private_health || 'unknown'  // v3.0.0: promoted, with fallback
      },
      super: deep_dive ? deep_dive.super : null,
      deductions: deep_dive ? deep_dive.deductions : null,
      health: deep_dive ? deep_dive.health : null,
      children: deep_dive ? deep_dive.children : null,
      life_events: deep_dive ? deep_dive.life_events : [],
      contact: {
        first_name,
        email,
        phone,
        consent_marketing,
        consent_disclaimer
      },
      request_security: {
        client_submission_id: client_submission_id || null,
        webhook_token_present: !!webhook_token,
        honeypot_triggered: !!honeypot_website
      },
      meta_source: {
        source: 'webflow_form',
        page_url: src.page_url || src._page_url || null,
        user_agent: triggerData.headers ? (triggerData.headers['user-agent'] || null) : null,
        form_version: formVersion,
      duplicate_guard_enabled: (config.dedupe_config || {}).enabled !== false
      }
    }
  }
};

const afterKeys = Object.keys(output.trigger.v1);

return [{
  json: {
    ...output,
    __audit: {
      node: NODE,
      ts: new Date().toISOString(),
      before_keys: beforeKeys,
      after_keys: afterKeys,
      added: afterKeys.filter(k => !beforeKeys.includes(k)),
      removed: beforeKeys.filter(k => !afterKeys.includes(k)),
      warnings,
      errors
    },
    meta: {
      run_id: runId,
      correlation_id: runId,
      valid: isValid,
      form_type: formType,
      warnings,
      errors,
      field_count: beforeKeys.length,
      validation_errors_count: errors.length,
      form_version: formVersion,
      duplicate_guard_enabled: (config.dedupe_config || {}).enabled !== false
    }
  }
}];

/**
 * PURPOSE
 * Prepares LLM payload for QUICK SNAPSHOT (sections 1-3).
 * v3.0.0: Now includes work_from_home, private_health, existing_benefits
 * which were promoted from deep dive to quick snapshot.
 *
 * INPUTS
 * - $input: trigger.v1 data from R2 route
 * - $('C1 Config'): validation config
 *
 * ADDITIONS
 * - llm_payload: structured prompt-ready data including promoted fields
 * - promoted_field_status: flags which promoted fields have real vs unknown values
 * - analysis_depth: "quick_snapshot"
 *
 * ROW GUIDE
 * Input: { trigger: { v1: { household: {...}, income: {...}, property: {...} } } }
 * Output: { trigger: ..., llm_payload: { analysis_depth, household, income, property }, ... }
 *
 * ASSUMPTIONS
 * - work_from_home is in tv1.income (may be "unknown" for v2 forms)
 * - private_health is in tv1.property (may be "unknown" for v2 forms)
 * - existing_benefits is in tv1.household (may be empty array)
 *
 * AUDIT
 * Hybrid: preserves run_id, passes contact separately
 */

const NODE = 'P2 Prepare Quick Analysis Payload';
const data = $input.item.json;
const tv1 = data.trigger.v1;
const runId = data.meta.run_id;

const llmPayload = {
  analysis_depth: 'quick_snapshot',
  household: tv1.household,       // includes existing_benefits (v3.0.0)
  income: tv1.income,             // includes work_from_home (v3.0.0)
  property: tv1.property          // includes private_health (v3.0.0)
};

// v3.0.0: Flag which promoted fields are available vs unknown
const promotedFieldStatus = {
  work_from_home: tv1.income.work_from_home !== 'unknown',
  private_health: tv1.property.private_health !== 'unknown',
  existing_benefits: (tv1.household.existing_benefits || []).length > 0
};

return [{
  json: {
    trigger: data.trigger,
    llm_payload: llmPayload,
    promoted_field_status: promotedFieldStatus,
    analysis_depth: 'quick_snapshot',
    contact: tv1.contact,
    __audit: {
      node: NODE,
      ts: new Date().toISOString(),
      warnings: [],
      errors: []
    },
    meta: {
      run_id: runId,
      correlation_id: runId,
      valid: true,
      form_type: 'quick_snapshot',
      form_version: data.meta.form_version || '2.0.0',
      warnings: [],
      errors: []
    }
  }
}];

/**
 * PURPOSE
 * Prepares LLM payload for DEEP DIVE (all sections 1-8).
 * v3.0.0: work_from_home and private_health are now in the base
 * trigger.v1 sections (income/property), NOT duplicated in deep_dive sub-objects.
 * Deep dive health section now only has phi_cover_level and phi_all_covered.
 * Deep dive deductions section no longer has work_from_home.
 *
 * INPUTS
 * - $input: trigger.v1 data from R2 route
 * - $('C1 Config'): validation config
 *
 * ADDITIONS
 * - llm_payload: full prompt-ready data including deep dive sections
 * - promoted_field_status: flags which promoted fields have real values
 * - analysis_depth: "deep_dive"
 *
 * ROW GUIDE
 * Input: { trigger: { v1: { household, income, property, super, deductions, health, children, life_events } } }
 * Output: { trigger: ..., llm_payload: { analysis_depth: "deep_dive", ... all sections ... }, ... }
 *
 * ASSUMPTIONS
 * - work_from_home is in tv1.income (base), not tv1.deductions (deep)
 * - private_health is in tv1.property (base), not tv1.health (deep)
 * - tv1.health now only contains phi_cover_level, phi_all_covered (detail fields)
 *
 * AUDIT
 * Hybrid: preserves run_id, passes contact separately
 */

const NODE = 'P3 Prepare Full Analysis Payload';
const data = $input.item.json;
const tv1 = data.trigger.v1;
const runId = data.meta.run_id;

const llmPayload = {
  analysis_depth: 'deep_dive',
  household: tv1.household,       // includes existing_benefits (v3.0.0)
  income: tv1.income,             // includes work_from_home (v3.0.0)
  property: tv1.property,         // includes private_health (v3.0.0)
  super: tv1.super || {},
  deductions: tv1.deductions || {},   // v3.0.0: no longer has work_from_home
  health: tv1.health || {},           // v3.0.0: only phi_cover_level, phi_all_covered
  children: tv1.children || {},
  life_events: tv1.life_events || []
};

// v3.0.0: All promoted fields are guaranteed available in deep dive
const promotedFieldStatus = {
  work_from_home: true,
  private_health: true,
  existing_benefits: (tv1.household.existing_benefits || []).length > 0
};

return [{
  json: {
    trigger: data.trigger,
    llm_payload: llmPayload,
    promoted_field_status: promotedFieldStatus,
    analysis_depth: 'deep_dive',
    contact: tv1.contact,
    __audit: {
      node: NODE,
      ts: new Date().toISOString(),
      warnings: [],
      errors: []
    },
    meta: {
      run_id: runId,
      correlation_id: runId,
      valid: true,
      form_type: 'deep_dive',
      form_version: data.meta.form_version || '2.0.0',
      warnings: [],
      errors: []
    }
  }
}];

/**
 * PURPOSE
 * Redacts PII from LLM payload. Preserves original contact separately.
 *
 * INPUTS
 * - $input: P2 or P3 output with llm_payload and contact
 *
 * ADDITIONS
 * - redacted_payload: PII-free version for LLM
 * - original_contact: preserved for downstream email/storage
 *
 * AUDIT
 * Tracks redaction count
 */

const NODE = 'P4 Redact PII For LLM';
const data = $input.item.json;
const runId = data.meta.run_id;
const originalContact = JSON.parse(JSON.stringify(data.contact));
let redactionCount = 0;

const emailRx = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}\b/g;
const phoneRx = /(\+?\d{1,3}[-.\s]?)?\(?\d{2,4}\)?[-.\s]?\d{2,4}[-.\s]?\d{2,4}/g;

function redactObj(obj) {
  if (obj === null || obj === undefined) return obj;
  if (typeof obj === 'string') {
    let r = obj.replace(emailRx, () => { redactionCount++; return '[EMAIL]'; });
    r = r.replace(phoneRx, () => { redactionCount++; return '[PHONE]'; });
    return r;
  }
  if (Array.isArray(obj)) return obj.map(i => redactObj(i));
  if (typeof obj === 'object') {
    const result = {};
    for (const [k, v] of Object.entries(obj)) {
      if (['first_name','email','phone','name'].includes(k)) { result[k] = '[REDACTED]'; redactionCount++; }
      else result[k] = redactObj(v);
    }
    return result;
  }
  return obj;
}

const redactedPayload = redactObj(JSON.parse(JSON.stringify(data.llm_payload)));

return [{
  json: {
    trigger: data.trigger,
    redacted_payload: redactedPayload,
    original_contact: originalContact,
    pii_map: { first_name: originalContact.first_name, email: originalContact.email, phone: originalContact.phone },
    analysis_depth: data.analysis_depth,
    __audit: { node: NODE, ts: new Date().toISOString(), redaction_count: redactionCount, warnings: [], errors: [] },
    meta: { run_id: runId, correlation_id: runId, valid: true, form_type: data.analysis_depth, warnings: [], errors: [] }
  }
}];

/**
 * PURPOSE
 * Parses L1 classification from merged HTTP response + P4 passthrough.
 * V3.1: Parses new status field per eligibility item (new_opportunity |
 *       confirm_existing | optimise_existing). Passes promoted fields
 *       (private_health, work_from_home, existing_benefits) through to L2.
 * Defensive: strips markdown fences, detects truncation.
 *
 * INPUTS
 * - $input: U2 Merge output (L1 response + P4 passthrough via combineByPosition)
 * - $('C1 Config'): config reference
 *
 * REMOVALS
 * - Raw OpenAI response fields replaced by structured l1_classification
 *
 * ADDITIONS
 * - l1_classification (object): parsed classification with status fields
 * - l1_usage (object): token counts
 *
 * ROW GUIDE
 * Input:  { choices: [{message:{content:"{...}"}}], usage: {...}, trigger: {...}, ...passthrough }
 * Output: { l1_classification: {...}, l1_usage: {...}, trigger: {...}, ...passthrough }
 *
 * ASSUMPTIONS
 * - OpenAI json_object mode returns clean JSON but we defend against fences
 * - U2 Merge combines L1 HTTP (input 0) + P4 passthrough (input 1)
 * - L1 now includes status field per item and wfh_deduction section
 *
 * AUDIT
 * Hybrid: validates required classification keys including new v3.1 fields.
 * Errors cascade to R3 â†’ DLQ via meta.valid.
 */
const NODE = 'N2 Parse L1 & Build L2 Context';
const m = $input.item.json;
const config = $('C1 Config').item.json;
const warnings = [];
const errors = [];
const runId = m.meta?.run_id || $execution.id || `${Date.now()}`;

function cleanLlmJson(raw) {
  if (typeof raw !== 'string') return raw;
  let s = raw.trim();
  s = s.replace(/^```(?:json)?\s*\n?/i, '').replace(/\n?\s*```\s*$/i, '');
  return s.trim();
}

let l1 = null;
const l1Usage = m.usage || null;

const stopReason = m.choices?.[0]?.finish_reason;
if (stopReason === 'length') {
  warnings.push('L1 truncated (max_tokens) â€” output may be incomplete');
}

try {
  const raw = m.choices?.[0]?.message?.content;
  if (!raw) throw new Error('No L1 content in choices[0].message.content');
  const cleaned = cleanLlmJson(raw);
  l1 = typeof cleaned === 'object' ? cleaned : JSON.parse(cleaned);

  // --- V3.1 key validation ---
  const cls = l1.classification || l1;
  const classKeys = Object.keys(cls);

  // Critical keys (must exist for L2 to function)
  ['tax_bracket', 'mls', 'deduction_categories'].forEach(f => {
    if (!classKeys.includes(f)) warnings.push('L1 missing key: ' + f);
  });

  // V3.1 new keys (desirable but not fatal)
  if (!cls.wfh_deduction) warnings.push('L1 missing v3.1 key: wfh_deduction');

  // Validate status fields exist on benefit items
  const benefitKeys = ['ftb_a', 'ftb_b', 'ccs'];
  benefitKeys.forEach(bk => {
    if (cls[bk] && !cls[bk].status) {
      warnings.push(`L1 ${bk} missing status field â€” defaulting to new_opportunity`);
      if (cls[bk]) cls[bk].status = 'new_opportunity';
    }
  });

  // Ensure classification is at top level for downstream
  if (l1.classification) {
    l1 = l1.classification;
  }

} catch (e) {
  errors.push('L1 parse failed: ' + e.message);
  l1 = { error: e.message };
}

return [{ json: {
  l1_classification: l1,
  l1_usage: l1Usage,
  trigger: m.trigger,
  redacted_payload: m.redacted_payload,
  original_contact: m.original_contact,
  pii_map: m.pii_map,
  analysis_depth: m.analysis_depth,
  // V3.1: pass promoted fields explicitly for L2 context
  existing_benefits: m.redacted_payload?.existing_benefits || m.existing_benefits || '',
  private_health: m.redacted_payload?.private_health || m.private_health || 'unknown',
  work_from_home: m.redacted_payload?.work_from_home || m.work_from_home || 'unknown',
  __audit: { node: NODE, ts: new Date().toISOString(), warnings, errors },
  meta: {
    run_id: runId,
    correlation_id: runId,
    valid: errors.length === 0,
    form_type: m.analysis_depth || 'quick_snapshot',
    l1_model: l1Usage?.model || config.llm_config.classification_model,
    l1_total_tokens: l1Usage ? (l1Usage.prompt_tokens + l1Usage.completion_tokens) : 0,
    warnings,
    errors
  }
}}];

/**
 * PURPOSE
 * Parses L2 Claude analysis from merged HTTP response + N2 passthrough.
 * V3.1: 4 post-LLM validation guardrails (Issues 5,7,8,11).
 *   G1: Priority/confidence labels vs dollar impact
 *   G2: Title figure must match calculation total
 *   G3: Motor vehicle method must recommend higher value
 *   G4: Strip FTB B secondary earner rows for single parents
 * Handles: markdown code fences, truncated JSON (max_tokens), nested content.
 *
 * INPUTS
 * - $input: U3 Merge output (L2 Anthropic response + N2 passthrough)
 * - $('C1 Config'): config reference
 *
 * REMOVALS
 * - Raw Anthropic response fields replaced by structured l2_analysis
 *
 * ADDITIONS
 * - l2_analysis (object): parsed + guardrail-corrected analysis
 * - l2_usage (object): token counts
 * - guardrail_corrections (array): log of any corrections applied
 *
 * ROW GUIDE
 * Input:  { content: [{type:"text", text:"..."}], usage: {...}, l1_classification: {...}, ...passthrough }
 * Output: { l2_analysis: {...}, l2_usage: {...}, l1_classification: {...}, ...passthrough }
 *
 * ASSUMPTIONS
 * - Claude may wrap JSON in ```json fences despite prompt instruction
 * - Claude may hit max_tokens and truncate mid-JSON (stop_reason: "max_tokens")
 * - U3 Merge combines L2 HTTP (input 0) + N2 passthrough (input 1)
 * - L2 returns V3.1 schema with status, affordability_flag, total_new/confirm splits
 *
 * AUDIT
 * Hybrid: validates required keys + applies 4 guardrails.
 * Tracks all corrections for downstream visibility.
 */
const NODE = 'N3 Parse L2 & Build L3 Context';
const m = $input.item.json;
const config = $('C1 Config').item.json;
const warnings = [];
const errors = [];
const corrections = [];
const runId = m.meta?.run_id || $execution.id || `${Date.now()}`;

// --- Helpers ---
function cleanLlmJson(raw) {
  if (typeof raw !== 'string') return raw;
  let s = raw.trim();
  s = s.replace(/^```(?:json)?\s*\n?/i, '').replace(/\n?\s*```\s*$/i, '');
  return s.trim();
}

function salvageTruncatedJson(raw) {
  let s = raw;
  const lastGoodComma = s.lastIndexOf('},');
  const lastGoodBrace = s.lastIndexOf('}');
  if (lastGoodComma > 0) {
    s = s.substring(0, lastGoodComma + 1);
  } else if (lastGoodBrace > 0) {
    s = s.substring(0, lastGoodBrace + 1);
  }
  let openBraces = 0, openBrackets = 0;
  for (const c of s) {
    if (c === '{') openBraces++;
    if (c === '}') openBraces--;
    if (c === '[') openBrackets++;
    if (c === ']') openBrackets--;
  }
  while (openBrackets > 0) { s += ']'; openBrackets--; }
  while (openBraces > 0) { s += '}'; openBraces--; }
  return JSON.parse(s);
}

function extractDollar(str) {
  if (typeof str === 'number') return str;
  if (typeof str !== 'string') return 0;
  const match = str.replace(/,/g, '').match(/-?\$?([\d.]+)/);
  return match ? parseFloat(match[1]) : 0;
}

function isSingleParent(payload) {
  const rel = (payload?.relationship_status || payload?.relationship || '').toLowerCase();
  const deps = parseInt(payload?.dependants || payload?.number_of_dependants || '0', 10);
  return deps > 0 && ['widowed', 'separated', 'single'].includes(rel);
}

// --- Parse L2 ---
let l2 = null;
const l2Usage = m.usage || null;
const stopReason = m.stop_reason;
const isTruncated = stopReason === 'max_tokens';
if (isTruncated) {
  warnings.push('L2 truncated (max_tokens) â€” attempting partial salvage');
}

try {
  const raw = m.content?.[0]?.text;
  if (!raw) throw new Error('No L2 content in content[0].text');
  const cleaned = cleanLlmJson(raw);
  try {
    l2 = JSON.parse(cleaned);
  } catch (parseErr) {
    if (isTruncated) {
      warnings.push('Clean parse failed â€” salvaging truncated JSON');
      l2 = salvageTruncatedJson(cleaned);
      warnings.push('Salvage succeeded â€” some sections may be incomplete');
    } else {
      throw parseErr;
    }
  }

  // --- V3.1 schema validation ---
  const criticalKeys = ['opportunities', 'risk_flags'];
  criticalKeys.forEach(f => {
    if (!l2[f]) warnings.push('L2 missing critical key: ' + f);
  });

  const importantKeys = ['lifestyle_scenarios', 'scenario_table', 'estimated_total_annual_savings'];
  importantKeys.forEach(f => {
    if (!l2[f]) warnings.push('L2 missing key: ' + f);
  });

  if (!l2.life_event_actions) warnings.push('L2 missing optional key: life_event_actions');
  if (!l2.meta) warnings.push('L2 missing key: meta');

  // Validate V3.1 status fields
  if (Array.isArray(l2.opportunities)) {
    const missingStatus = l2.opportunities.filter(o => !o.status);
    if (missingStatus.length > 0) {
      warnings.push(`${missingStatus.length} opportunities missing status field â€” defaulting to new_opportunity`);
      missingStatus.forEach(o => { o.status = 'new_opportunity'; });
    }
  }

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // GUARDRAIL G1: Priority/confidence vs dollar impact
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  if (Array.isArray(l2.opportunities)) {
    l2.opportunities.forEach(opp => {
      const val = typeof opp.estimated_annual_value === 'number'
        ? opp.estimated_annual_value
        : extractDollar(opp.estimated_annual_value);
      const before = opp.confidence;

      if (val >= 1000 && opp.confidence !== 'high') {
        opp.confidence = 'high';
        corrections.push(`G1: ${opp.title} â€” confidence ${before}â†’high (value $${val} >= $1,000)`);
      } else if (val < 200 && opp.confidence === 'high') {
        opp.confidence = 'low';
        corrections.push(`G1: ${opp.title} â€” confidence highâ†’low (value $${val} < $200)`);
      } else if (val >= 200 && val < 1000 && opp.confidence !== 'medium') {
        // Only correct if clearly wrong (high for small value or low for medium value)
        if (opp.confidence === 'low' && val >= 200) {
          opp.confidence = 'medium';
          corrections.push(`G1: ${opp.title} â€” confidence lowâ†’medium (value $${val} in $200-$999 range)`);
        }
      }
    });
  }

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // GUARDRAIL G2: Title figure must match calc total
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  if (Array.isArray(l2.opportunities)) {
    l2.opportunities.forEach(opp => {
      if (!opp.calculation_rows || opp.calculation_rows.length === 0) return;
      const lastRow = opp.calculation_rows[opp.calculation_rows.length - 1];
      const calcTotal = extractDollar(lastRow.amount);
      const titleVal = typeof opp.estimated_annual_value === 'number'
        ? opp.estimated_annual_value
        : extractDollar(opp.estimated_annual_value);

      // Allow 5% tolerance for rounding
      if (calcTotal > 0 && titleVal > 0) {
        const diff = Math.abs(calcTotal - titleVal);
        const tolerance = calcTotal * 0.05;
        if (diff > tolerance && diff > 10) {
          corrections.push(`G2: ${opp.title} â€” title $${titleVal} â‰  calc total $${calcTotal}, using calc total`);
          opp.estimated_annual_value = calcTotal;
        }
      }
    });
  }

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // GUARDRAIL G3: Motor vehicle method selection
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  if (Array.isArray(l2.opportunities)) {
    l2.opportunities.forEach(opp => {
      const titleLower = (opp.title || '').toLowerCase();
      if (!titleLower.includes('motor') && !titleLower.includes('vehicle') && !titleLower.includes('car')) return;
      if (!opp.calculation_rows || opp.calculation_rows.length < 2) return;

      let centsPerKm = 0;
      let logbook = 0;
      opp.calculation_rows.forEach(row => {
        const label = (row.label || '').toLowerCase();
        const amt = extractDollar(row.amount);
        if (label.includes('cents') || label.includes('c/km') || label.includes('88c') || label.includes('per km')) {
          centsPerKm = amt;
        }
        if (label.includes('logbook')) {
          logbook = amt;
        }
      });

      if (centsPerKm > 0 && logbook > 0) {
        const descLower = (opp.description || '').toLowerCase();
        const claimLower = (opp.how_to_claim || '').toLowerCase();
        const recommendsLogbook = descLower.includes('logbook') || claimLower.includes('logbook');
        const recommendsCpk = descLower.includes('cents per') || claimLower.includes('cents per');

        if (centsPerKm > logbook && recommendsLogbook && !recommendsCpk) {
          corrections.push(`G3: Motor vehicle â€” logbook $${logbook} < cents/km $${centsPerKm}, but logbook was recommended. Flagging.`);
          warnings.push(`Motor vehicle recommendation may be suboptimal: cents/km ($${centsPerKm}) > logbook ($${logbook})`);
        } else if (logbook > centsPerKm && recommendsCpk && !recommendsLogbook) {
          corrections.push(`G3: Motor vehicle â€” cents/km $${centsPerKm} < logbook $${logbook}, but cents/km was recommended. Flagging.`);
          warnings.push(`Motor vehicle recommendation may be suboptimal: logbook ($${logbook}) > cents/km ($${centsPerKm})`);
        }
      }
    });
  }

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // GUARDRAIL G4: Strip FTB B secondary earner rows for single parents
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  const singleParent = isSingleParent(m.redacted_payload);
  if (singleParent && Array.isArray(l2.opportunities)) {
    l2.opportunities.forEach(opp => {
      const titleLower = (opp.title || '').toLowerCase();
      if (!titleLower.includes('ftb b') && !titleLower.includes('family tax benefit part b')) return;

      if (opp.calculation_rows) {
        const before = opp.calculation_rows.length;
        opp.calculation_rows = opp.calculation_rows.filter(row => {
          const label = (row.label || '').toLowerCase();
          return !label.includes('secondary earner') && !label.includes('second earner');
        });
        const removed = before - opp.calculation_rows.length;
        if (removed > 0) {
          corrections.push(`G4: FTB B â€” removed ${removed} secondary earner rows (single parent household)`);
        }
      }

      // Also clean description
      if (opp.eligibility_reason) {
        opp.eligibility_reason = opp.eligibility_reason
          .replace(/secondary earner income test[^.]*\./gi, '')
          .replace(/\s{2,}/g, ' ')
          .trim();
      }
    });
  }

  // --- Validate opportunities quality ---
  if (Array.isArray(l2.opportunities)) {
    const missingCalcRows = l2.opportunities.filter(o => !o.calculation_rows || o.calculation_rows.length === 0);
    if (missingCalcRows.length > 0) {
      warnings.push(`${missingCalcRows.length} opportunities missing calculation_rows`);
    }
  }

} catch (e) {
  errors.push('L2 parse failed: ' + e.message);
  l2 = { error: e.message };
}

return [{ json: {
  l2_analysis: l2,
  l2_usage: l2Usage,
  l1_classification: m.l1_classification,
  l1_usage: m.l1_usage,
  trigger: m.trigger,
  redacted_payload: m.redacted_payload,
  original_contact: m.original_contact,
  pii_map: m.pii_map,
  analysis_depth: m.analysis_depth,
  existing_benefits: m.existing_benefits || '',
  guardrail_corrections: corrections,
  __audit: {
    node: NODE,
    ts: new Date().toISOString(),
    truncation_detected: isTruncated,
    guardrails_applied: corrections.length,
    warnings,
    errors
  },
  meta: {
    run_id: runId,
    correlation_id: runId,
    valid: errors.length === 0,
    form_type: m.analysis_depth || 'quick_snapshot',
    l2_model: config.llm_config.analysis_model,
    l2_input_tokens: l2Usage?.input_tokens || 0,
    l2_output_tokens: l2Usage?.output_tokens || 0,
    l2_truncated: isTruncated,
    opportunity_count: Array.isArray(l2?.opportunities) ? l2.opportunities.length : 0,
    risk_flag_count: Array.isArray(l2?.risk_flags) ? l2.risk_flags.length : 0,
    estimated_total: l2?.estimated_total_annual_savings || null,
    guardrail_corrections_count: corrections.length,
    warnings,
    errors
  }
}}];

/**
 * PURPOSE
 * Normalises all 3 LLM outputs into unified report schema.
 * V3.1: Splits opportunities into confirm_existing[] vs new_opportunities[],
 *       calculates two separate totals (hero = new only), adds affordability_flag
 *       to salary sacrifice items, and validates acronym expansion.
 * Assembles final deliverable for P5 email render.
 *
 * INPUTS
 * - $input: U4 Merge output (L3 response + N3 passthrough with l1/l2)
 * - $('C1 Config'): config reference
 *
 * REMOVALS
 * - Raw OpenAI L3 response fields replaced by unified report
 *
 * ADDITIONS
 * - report (object): unified report with sections, classification, detailed_analysis
 * - report.confirm_existing (array): benefits user likely already receives
 * - report.new_opportunities (array): genuinely new optimisation strategies
 * - report.total_new_opportunities (number): hero figure
 * - report.total_confirm_existing (number): secondary callout figure
 * - report_meta (object): completion status, section counts, key metrics
 * - token_usage (object): l1/l2/l3 token counts for cost tracking
 *
 * ROW GUIDE
 * Input:  { choices: [...], usage: {...}, l1_classification: {...}, l2_analysis: {...}, ...passthrough }
 * Output: { report: {...}, report_meta: {...}, token_usage: {...}, original_contact: {...} }
 *
 * ASSUMPTIONS
 * - L3 GPT json_object mode returns clean JSON; defend against fences
 * - L3 sections follow V3.1 schema: your_snapshot, tax_position, new_opportunities,
 *   confirm_existing, risk_alerts, lifestyle_scenarios
 * - OR L3 may return single "opportunities" section with status field per item (fallback)
 * - L2 may have been salvaged from truncation (check meta.l2_truncated)
 * - isComplete = all 3 LLMs succeeded AND <=1 section missing from expected set
 *
 * AUDIT
 * Hybrid: validates all 3 LLM outputs present, splits confirm/new, checks affordability,
 * validates acronym expansion. meta.valid=false if any LLM failed or >1 section missing â†’ R3 â†’ DLQ.
 */
const NODE = 'N4 Normalise Report Structure';
const m = $input.item.json;
const config = $('C1 Config').item.json;
const runId = m.meta?.run_id || $execution.id || `${Date.now()}`;
const warnings = [];
const errors = [];

// === HELPERS ===

function cleanLlmJson(raw) {
  if (typeof raw !== 'string') return raw;
  let s = raw.trim();
  s = s.replace(/^```(?:json)?\s*\n?/i, '').replace(/\n?\s*```\s*$/i, '');
  return s.trim();
}

function extractDollar(str) {
  if (typeof str === 'number') return str;
  if (typeof str !== 'string') return 0;
  const match = str.replace(/,/g, '').match(/-?\$?([\d.]+)/);
  return match ? parseFloat(match[1]) : 0;
}

function estimateFortnightlyIncome(payload) {
  const midpoints = {
    under_45k: 32000, '45k_65k': 55000, '65k_90k': 77500,
    '90k_120k': 105000, '120k_180k': 150000, '180k_250k': 215000, '250k_plus': 300000
  };
  const band = payload?.household_income || payload?.income_band || '';
  const annual = midpoints[band] || 77500;
  return annual / 26; // fortnightly
}

// === PARSE L3 ===
let l3 = null, l3Usage = null;
const l3StopReason = m.choices?.[0]?.finish_reason;
if (l3StopReason === 'length') warnings.push('L3 truncated (max_tokens)');

try {
  const raw = m.choices?.[0]?.message?.content;
  if (!raw) throw new Error('No L3 content in choices[0].message.content');
  const cleaned = cleanLlmJson(raw);
  l3 = typeof cleaned === 'object' ? cleaned : JSON.parse(cleaned);
  l3Usage = m.usage || null;
} catch (e) {
  errors.push('L3 parse failed: ' + e.message);
  l3 = { error: e.message };
}

// === GATHER UPSTREAM ===
const l1 = m.l1_classification || {};
const l2 = m.l2_analysis || {};
const l2Truncated = m.meta?.l2_truncated || false;

// === V3.1 SECTION VALIDATION ===
// L3 may output either V3.1 schema (new_opportunities + confirm_existing)
// or V3 schema (single opportunities) â€” handle both
const sections = l3?.sections || {};
const sectionKeys = Object.keys(sections);

// Core expected sections (V3.1)
const coreExpected = ['your_snapshot', 'tax_position', 'risk_alerts', 'lifestyle_scenarios'];
// Opportunity sections â€” either split (V3.1) or single (V3 fallback)
const hasSplitOpps = sectionKeys.includes('new_opportunities') || sectionKeys.includes('confirm_existing');
const hasSingleOpps = sectionKeys.includes('opportunities');

const coreMissing = coreExpected.filter(s => !sectionKeys.includes(s));
if (coreMissing.length > 0) warnings.push('Missing report sections: ' + coreMissing.join(', '));
if (!hasSplitOpps && !hasSingleOpps) warnings.push('Missing: no opportunities section found');

// Warn on legacy V2 sections
const legacySections = ['government_benefits', 'super_health_check', 'next_steps'];
const unexpectedLegacy = legacySections.filter(s => sectionKeys.includes(s));
if (unexpectedLegacy.length > 0) {
  warnings.push('Unexpected legacy V2 sections found: ' + unexpectedLegacy.join(', '));
}

// === LLM VALIDATION ===
const hasL1 = l1 && !l1.error;
const hasL2 = l2 && !l2.error && (l2.opportunities || l2.risk_flags);
const hasL3 = l3 && !l3.error && l3.sections;

// Allow 1 missing core section (lifestyle_scenarios nice-to-have)
const effectiveMissing = coreMissing.filter(s => s !== 'lifestyle_scenarios');
const isComplete = hasL1 && hasL2 && hasL3 && effectiveMissing.length === 0;

if (!hasL1) errors.push('L1 classification failed');
if (!hasL2) errors.push('L2 analysis failed');
if (!hasL3) errors.push('L3 narrative failed');
if (l2Truncated && hasL2) warnings.push('L2 was truncated but salvaged â€” some analysis may be incomplete');

// === SPLIT OPPORTUNITIES INTO CONFIRM vs NEW ===
let newOpportunities = [];
let confirmExisting = [];

if (hasSplitOpps) {
  // V3.1: L3 already split them
  newOpportunities = sections.new_opportunities?.items || [];
  confirmExisting = sections.confirm_existing?.items || [];
} else if (hasSingleOpps && sections.opportunities?.items) {
  // V3 fallback: single list â€” split by status field
  sections.opportunities.items.forEach(item => {
    if (item.status === 'confirm_existing') {
      confirmExisting.push(item);
    } else {
      // new_opportunity, optimise_existing, or missing status all go to new
      newOpportunities.push(item);
    }
  });
  warnings.push('L3 returned single opportunities section â€” split by status field in N4');
}

// === CALCULATE TWO SEPARATE TOTALS ===
const totalNew = newOpportunities.reduce((sum, o) => {
  return sum + extractDollar(o.estimated_annual_value);
}, 0);

const totalConfirm = confirmExisting.reduce((sum, o) => {
  return sum + extractDollar(o.estimated_annual_value);
}, 0);

// Use L2/L3 totals as fallback if available
const totalNewFinal = totalNew || l2.total_new_opportunities || l3?.total_new_opportunities || 0;
const totalConfirmFinal = totalConfirm || l2.total_confirm_existing || l3?.total_confirm_existing || 0;
const estimatedTotal = totalNewFinal + totalConfirmFinal;

// === AFFORDABILITY FLAG FOR SALARY SACRIFICE ===
const fortnightlyIncome = estimateFortnightlyIncome(m.redacted_payload);
const allOpps = [...newOpportunities, ...confirmExisting];
allOpps.forEach(opp => {
  const titleLower = (opp.title || '').toLowerCase();
  if (!titleLower.includes('salary sacrifice') && !titleLower.includes('salary sacrific')) return;

  // Check if L2 already set affordability_flag
  if (opp.affordability_flag === true) return;

  // Try to extract fortnightly reduction from affordability_warning or calculation_rows
  let fortnightlyReduction = 0;
  if (opp.affordability_warning) {
    const match = opp.affordability_warning.replace(/,/g, '').match(/\$(\d+(?:\.\d+)?)/);
    if (match) fortnightlyReduction = parseFloat(match[1]);
  }
  if (!fortnightlyReduction && opp.calculation_rows) {
    opp.calculation_rows.forEach(row => {
      const label = (row.label || '').toLowerCase();
      if (label.includes('fortnightly') && label.includes('reduction')) {
        fortnightlyReduction = extractDollar(row.amount);
      }
    });
  }
  // Estimate: annual sacrifice / 26
  if (!fortnightlyReduction) {
    const annualVal = extractDollar(opp.estimated_annual_value);
    // Rough: the sacrifice amount is typically 2-3x the benefit value
    // Use a conservative estimate from the annual value
    fortnightlyReduction = (annualVal * 2.5) / 26;
  }

  if (fortnightlyReduction > 0 && fortnightlyIncome > 0) {
    const threshold = fortnightlyIncome * 0.10;
    if (fortnightlyReduction > threshold) {
      opp.affordability_flag = true;
      if (!opp.affordability_warning) {
        opp.affordability_warning = `This strategy may reduce your fortnightly take-home pay by approximately $${Math.round(fortnightlyReduction)}. Super contributions generally cannot be accessed until age 60. Consider whether your household budget can absorb this reduction.`;
      }
      warnings.push(`Affordability flag set on salary sacrifice: $${Math.round(fortnightlyReduction)}/fn > 10% of $${Math.round(fortnightlyIncome)}/fn`);
    }
  }
});

// === ACRONYM EXPANSION CHECK ===
const acronymPatterns = [
  { pattern: /\bFTB\b(?!\s*[A-B]?\s*\()/, expanded: 'Family Tax Benefit' },
  { pattern: /\bCCS\b(?!\s*\()/, expanded: 'Child Care Subsidy' },
