  return `<span style="display:inline-block;width:8px;height:8px;border-radius:50%;background:${c};margin-right:4px;vertical-align:middle;"></span><span style="color:${c};font-size:11px;font-weight:bold;letter-spacing:0.5px;vertical-align:middle;">${labels[level] || 'MEDIUM IMPACT'}</span>`;
}

function severityTag(severity) {
  const c = {
    high: { bg: '#fef2f2', text: '#991b1b', icon: 'ğŸš¨' },
    medium: { bg: '#fffbeb', text: '#92400e', icon: 'âš ï¸' },
