  };
  const x = c[severity] || c.medium;
  return `<span style="background:${x.bg};color:${x.text};padding:2px 8px;border-radius:4px;font-size:11px;font-weight:bold;">${x.icon} ${(severity || 'medium').toUpperCase()}</span>`;
}

/**
 * V3.1 ENHANCED CALC TABLE
 * - Detects +/â€“ prefixes in labels and styles accordingly
 * - Detects subtotal/total rows and adds appropriate borders
 * - Bold total rows with green background
 * - Subtotal rows get lighter border
 */
function calcTable(rows) {
  if (!rows || rows.length === 0) return '';
  const isLastRow = (i) => i === rows.length - 1;

  function isSubtotal(label) {
    const l = (label || '').toLowerCase();
    return l.includes('subtotal') || l.startsWith('sub-total');
  }

  function isTotal(label, idx) {
    if (isLastRow(idx)) return true;
    const l = (label || '').toLowerCase();
    return l.startsWith('total') || l.startsWith('estimated annual') || l.startsWith('estimated total')
      || l.startsWith('net ') || l.includes('total ');
  }

  function isDeduction(label, amount) {
    const l = (label || '').toLowerCase();
    const a = (amount || '').trim();
    return l.startsWith('â€“') || l.startsWith('- ') || l.startsWith('minus')
      || a.startsWith('â€“') || a.startsWith('-$') || a.startsWith('-');
  }

  function isAddition(label, amount) {
    const l = (label || '').toLowerCase();
    const a = (amount || '').trim();
    return l.startsWith('+') || l.startsWith('plus') || a.startsWith('+');
  }

  let html = '<table style="width:100%;border-collapse:collapse;margin:8px 0;" cellpadding="0" cellspacing="0">';

  rows.forEach((row, i) => {
    const label = row.label || '';
    const amount = row.amount || '';
    const isTotalRow = isTotal(label, i);
    const isSubtotalRow = isSubtotal(label);
    const isDeductionRow = isDeduction(label, amount);
    const isAdditionRow = isAddition(label, amount);

    // Styling
    let weight = 'normal';
    let topBorder = '';
    let bg = i % 2 === 0 ? 'background:#f9fafb;' : '';
    let labelColor = '#374151';
    let amountColor = '#111827';

    if (isTotalRow) {
      weight = 'bold';
      topBorder = 'border-top:2px solid #374151;';
      bg = 'background:#f0fdf4;';
      amountColor = '#065f46';
    } else if (isSubtotalRow) {
      weight = '600';
      topBorder = 'border-top:1px solid #9ca3af;';
      bg = 'background:#f9fafb;';
    }

    // Amount colour for deductions/additions
    if (isDeductionRow && !isTotalRow) {
      amountColor = '#dc2626';
    } else if (isAdditionRow && !isTotalRow) {
      amountColor = '#059669';
    }

    // Indent intermediate rows (not first, not total, not subtotal)
    const indent = (!isTotalRow && !isSubtotalRow && i > 0) ? 'padding-left:18px;' : '';

    html += `<tr style="${bg}">
      <td style="padding:6px 10px;${indent}font-size:13px;color:${labelColor};font-weight:${weight};${topBorder}">${esc(label)}</td>
      <td style="padding:6px 10px;font-size:13px;color:${amountColor};font-weight:${weight};text-align:right;white-space:nowrap;${topBorder}">${esc(amount)}</td>
    </tr>`;
  });

  html += '</table>';
  return html;
}

function stepsBlock(text) {
  if (!text) return '';
  return `<div style="background:#f9fafb;padding:10px 14px;margin:8px 0;border-radius:4px;font-size:13px;color:#374151;line-height:1.6;">
    <strong style="color:#065f46;">How to claim:</strong><br>${esc(text)}
  </div>`;
}

/** V3.1: Styled CTA button (not bare link) */
function ctaButton(url, label) {
