  return `<a href="${esc(url)}" target="_blank" rel="noopener" style="display:inline-block;background:#10b981;color:#ffffff;padding:10px 20px;border-radius:5px;font-size:13px;font-weight:bold;text-decoration:none;margin-top:10px;">${btnLabel}</a>`;
}

function relatedBenefitsBlock(items) {
  if (!items || items.length === 0) return '';
  let html = '<div style="background:#eff6ff;border-left:3px solid #3b82f6;padding:8px 12px;margin:8px 0;border-radius:0 4px 4px 0;"><strong style="color:#1e40af;font-size:12px;">ğŸ’¡ How this connects to your other benefits:</strong><ul style="margin:4px 0 0 16px;padding:0;font-size:12px;color:#374151;line-height:1.5;">';
  items.forEach(item => { html += `<li>${esc(item)}</li>`; });
  html += '</ul></div>';
  return html;
}

function categoryIcon(cat) {
  const icons = { government_benefit: 'ğŸ›ï¸', tax_deduction: 'ğŸ§¾', superannuation: 'ğŸ¦', investment: 'ğŸ“ˆ', compliance: 'âš–ï¸' };
